import React from 'react';
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
import axios from 'axios';


// modal class for adding new expert 
import './modal.css'
class ModalE extends React.Component {
  // initial state 
  constructor(props) {
    super(props);
    this.state = {
      isLoading: false, // variable pour l'animation du button 
      modal: false,
      name: "",// nom d'expert
      Inspecteur:[{
        NomPrenom:"",
        Qualification:""
    },],
    detailsMission:[{
        dateDebut:"",
        dateFin:"",
        dureeVisite:"",
        qualificationExigees:"",
    },],
    detailsProjet:[{
        lieuMission:"",
        client:""
    },],
    etendueMission:[{
        etendue:"",
    },],
    logistique:[{
        materiels:"",
        lieu:"",
        mobilisation:"",
        logement:"",
        demobilisation:"",

    },],
    
   
    
    }

    this.toggle = this.toggle.bind(this); // close modal after adding new expert
  }


  // toggle get state empty after adding an expert, close modal after add expert 
  toggle() {
    // clear state 
    this.setState(prevState => ({
      modal: !prevState.modal,
      name: "",
      Inspecteur:[{
          NomPrenom:"",
          Qualification:""
      },],
      detailsMission:[{
          dateDebut:"",
          dateFin:"",
          dureeVisite:"",
          qualificationExigees:"",
      },],
      detailsProjet:[{
          lieuMission:"",
          client:""
      },],
      etendueMission:[{
          etendue:"",
      },],
      logistique:[{
          materiels:"",
          lieu:"",
          mobilisation:"",
          logement:"",
          demobilisation:"",

      },],
      
     
      
      
      
    }));
  }
  // onChange get value from input
  onChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value
    });
 




};



AddMission = () =>{
    this.setState({ isLoading: true})
    setTimeout(() => {
      this.setState(prevState => ({
        modal: !prevState.modal,
        isLoading: false,
      }));
    }, 1200)

    axios
      .post("/addd",{  // envoyer les données récupérées a la base de données au chemin /add
        name: this.state.name,
            NomPrenom:this.state.NomPrenom,
            Qualification:this.state.Qualification,
            dateDebut:this.state.dateDebut,
            dateFin:this.state.dateFin,
            dureeVisite:this.state.dureeVisite,
            qualificationExigees:this.state.qualificationExigees,
            lieuMission:this.state.lieuMission,
            client:this.state.client,
            etendue:this.state.etendue,
            materiels:this.state.materiels,
            lieu:this.state.lieu,
            mobilisation:this.state.mobilisation,
            logement:this.state.logement,
            demobilisation:this.state.demobilisation,
  
       
        
       
        
        
        })
      .then(res => axios.get("/missionsList") 
      //.then(res => this.props.updateExpertList(res.data))
      )
      .catch(err => alert(err));
  }



  render() {
    return (
      <div>
        <button className="homeButton" onClick={this.toggle}>Ajouter expert</button>
        <Modal size='lg' isOpen={this.state.modal} toggle={this.toggle} className={this.props.className}>
          <ModalHeader toggle={this.toggle}>Ajouter un expert</ModalHeader>
          <ModalBody>
            
            <div>

            <label>Nom et Prenom</label><input  name="NomPrenom"type="text"   onChange={this.onChange}/>
            <label>Nom et Prenom</label><input  name="NomPrenom"type="text"   onChange={this.onChange}/>
            </div>
            
         
            
          </ModalBody>
          <ModalFooter>
    <Button 
          disabled={this.state.isLoading} color="primary" 
          onClick={()=> {this.AddMission(); }}
          >
        { !this.state.isLoading &&  <span> Ajouter un expert</span>}
        { this.state.isLoading &&  <span><i class="fas fa-circle-notch fa-spin"></i> Ajouter un expert</span>}
    
    </Button>
            <Button color="secondary" onClick={this.toggle}>Annuler</Button>
          </ModalFooter>
        </Modal>
      </div>
    );
  }
}

export default ModalE;
