import React, { Component } from 'react'
import axios from 'axios';

export default class MissionCard extends Component {
    state = { 
        profil:[], // initial variable to put extracting data from database named FalconData from collection named experts
    }

    // componentDidMount finction that get data from database
componentDidMount () {
    axios
    .get("/missionList")
    .then(res => this.setState({
        profil : res.data    // get data from database named FalconData and put all data in variable called profile
    }))
    .catch(err =>console.log("err", err))
  }
    render() {
        return (
            <div>
            {this.state.profil
                .map((el, i) =>{
                    // pass data as a props to expertCard container
                    return  <missi key={i} data={el}  key={el._id}/> 
                })
            } 
            </div>
        )
    }
}
