import React, { Component } from 'react'
import axios from 'axios';

export class Clients extends Component {
    constructor(props) {
        super(props)
    
        this.state = {
            clientCode:"",
            clientName:"",
             tab:[]
        }
    }
    onChange = (e) => {
        this.setState({
          [e.target.name]: e.target.value,
          
        });}

        componentDidMount (){

            axios
              .get("/clients")
              .then(res => this.setState({
                  tab : res.data  // get data from database named FalconData and put all data in variable called profile
              }))
              .catch(err =>console.log("err", err))
          }
    addclient = () => {
        const { firstnameR,lastnameR,pictureR, emailR, passwordR } = this.state;
        axios
        .post("/addClient",{
            clientName:this.state.clientName,
            clientCode:this.state.clientCode,
        })
        .then(res => axios.get("/clients") 
      //.then(res => this.props.updateExpertList(res.data))
      )
      .catch(err => alert(err));
    }
    
    render() {
        return (
            <div>
                <label>nom</label><input type="text" name="clientName" onChange={this.onChange}></input>
                <label>code</label><input type="text" name="clientCode" onChange={this.onChange}></input>
                <button onClick={this.addclient}>add</button>
            </div>
        )
    }
}

export default Clients
