import React, { Component } from 'react'
import { Page, Text, View, Document, StyleSheet, Image, } from '@react-pdf/renderer';
import { PDFViewer } from '@react-pdf/renderer';
import axios from 'axios';


export default class PrintOrdre extends Component {
    constructor(props) {
        super(props)
    
        this.state = {
            numOrdre: "",// nom d'expert
            NomPrenom:"",
            Qualification:"",       
            dateDebut:"",
            dateFin:"",
            dureeVisite:"",
            qualificationExigees:"",
            lieuMission:"",
            client:"",
            etendue:"",
            materiels:"",
            lieu:"",
            mobilisation:"",
            logement:"",
            demobilisation:"",
          }     
        
    }
    // get data from data base 
    componentDidMount () {
        axios.get("/missionsList/" + this.props.id ).then(res => this.setState({
          ...res.data  // obtenir toutes variables de la base de donnés et les envoyer au constructor 
        }
          ))}
    
    render() {
        return (
            <div  style={style.container}>
                 <PDFViewer width="100%" height="900px">
                 <Document>
                        <Page size="A4" style={style.page} >
                            <View style={style.body}>
                                <Image
                                    style={style.image}
                                    src={require('../Media/logo-falcon.png')}
                                />

                            </View>
            
                            {/* <View>
                              
                            <Text style={style.title}>lieu: {this.state.lieu}</Text>
                                    <Text style={style.title}>ordre : {this.state.numOrdre}</Text>
                                   
                                </View>
                                <View wrap={false}>
                                    <Text style={style.descriptions}>Experience</Text>
                                    
                                </View>

                                <Text style={style.textFooter}>Generated by Falcon</Text> */}
                        </Page>




                        <View style={style.table}> {/* TableHeader */} 
                        <View style={style.tableRow}> <View style={style.tableCol}> 
                        <Text style={style.tableCell}>Product</Text> </View> 
                        <View style={style.tableCol}> <Text style={style.tableCell}>Type</Text> </View> 
                        <View style={style.tableCol}> <Text style={style.tableCell}>Period</Text> </View>
                         <View style={style.tableCol}> <Text style={style.tableCell}>Price</Text>
                         </View> </View> {/* TableContent */} <View style={style.tableRow}>
                              <View style={style.tableCol}> 
                              <Text style={style.tableCell}>React-PDF</Text> </View>
                               <View style={style.tableCol}> 
                               <Text style={style.tableCell}>3 User </Text> </View> 
                               <View style={style.tableCol}>
                                    <Text style={style.tableCell}>2019-02-20 - 2020-02-19</Text> </View>
                                     <View style={style.tableCol}> <Text style={style.tableCell}>5€</Text>
                                      </View> </View> </View>





                </Document>
                </PDFViewer>
                
            </div>
        )
    }
}
const style = StyleSheet.create({

    table: { 
        display: "table",
         width: "auto", 
         borderStyle: "solid", 
         borderWidth: 1,
          borderRightWidth: 0,
           borderBottomWidth: 0 },
     tableRow: { 
         margin: "auto",
          flexDirection: "row" 
        }, 
    tableCol:
     { width: "25%",
      borderStyle: "solid",
       borderWidth: 1,
        borderLeftWidth: 0,
         borderTopWidth: 0 
    },
     tableCell: {
          margin: "auto",
           marginTop: 5,
            fontSize: 10 },
    page:{
      
       
    },
    body: {
      },
    title: {
    },
    info: {
            
    },
    descriptions:{
        
    },
     descriptionsText:{     
    },
    options:{
            },
    container: {
        
    },
    image: {

        width:90,
        height:45,
    },
    hr:{
        
    },
    textFooter:{
        
    },
  });
