import React from 'react';
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
import axios from 'axios';
import "./ordreMission.css"


// modal class for adding new expert 
import './modal.css'
import Clients from './Clients';
class ModalE extends React.Component {
  // initial state 
  constructor(props) {
    super(props);
    this.state = {
      isLoading: false, // variable pour l'animation du button 
      modal: false,
      numOrdre: "",
        NomPrenom:"",
        Qualification:"",
        dateDebut:"",
        dateFin:"",
        dureeVisite:"",
        qualificationExigees:"",
        lieuMission:"",
        client:"",
        codeclient:"",
        etendue:"",
        materiels:"",
        lieu:"",
        mobilisation:"",
        logement:"",
        demobilisation:"",
        in:100,
        Max:[],
        

    
    
   
    
    }

    this.toggle = this.toggle.bind(this); // close modal after adding new expert
  }


  // toggle get state empty after adding an expert, close modal after add expert 
  toggle() {
    // clear state 
    this.setState(prevState => ({
      modal: !prevState.modal,
      numOrdre: "",
          NomPrenom:"",
          Qualification:"",
          dateDebut:"",
          dateFin:"",
          dureeVisite:"",
          qualificationExigees:"",
          lieuMission:"",
          client:"",
          codeclient:"",
          etendue:"",
          materiels:"",
          lieu:"",
          mobilisation:"",
          logement:"",
          demobilisation:"",
          cc:[]
         
          
          
          

      
      
     
      
      
      
    }));
  }
  // onChange get value from input
  onChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value,
      
    });
    
 




};
componentDidMount (){

  axios
    .get("/missionsList")
    .then(res => this.setState({
        Max : res.data  // get data from database named FalconData and put all data in variable called profile
    }))
    .catch(err =>console.log("err", err))
    
}



AddMission = () =>{
  if (this.state.numOrdre===""||this.state.NomPrenom===""||this.state.Qualification==="")
  alert("veuillez remplir tout les champs");
  else{
    this.setState({ isLoading: true})
    setTimeout(() => {
      this.setState(prevState => ({
        modal: !prevState.modal,
        isLoading: false,
      }));
    }, 1200)

    axios
      .post("/addd",{  // envoyer les données récupérées a la base de données au chemin /addd
        numOrdre: this.state.numOrdre,
            NomPrenom:this.state.NomPrenom,
            Qualification:this.state.Qualification,
            dateDebut:this.state.dateDebut,
            dateFin:this.state.dateFin,
            dureeVisite:this.state.dureeVisite,
            qualificationExigees:this.state.qualificationExigees,
            lieuMission:this.state.lieuMission,
            client:this.state.client,
            codeclient:this.state.codeclient,
            etendue:this.state.etendue,
            materiels:this.state.materiels,
            lieu:this.state.lieu,
            mobilisation:this.state.mobilisation,
            logement:this.state.logement,
            demobilisation:this.state.demobilisation,      
            isChooseclient:this.state.isChooseclient,
            
        })
      .then(res => axios.get("/missionsList") 
      //.then(res => this.props.updateExpertList(res.data))
      )
      .catch(err => alert(err));
 } }

 classSelect = e => {
  var year= new Date().getFullYear();
  var bb=this.state.Max.length+1;
  if (e.target.value === 'client1') {
    this.setState({
      client: "client1",
      numOrdre:"001"+"-"+year+"-"+bb,
      [e.target.name]: e.target.value,
    });
  } else if (e.target.value === 'client2')
    this.setState({
      client: "client2",
      numOrdre:"005"+"-"+year+"-"+bb,
      [e.target.name]: e.target.value
      
    });
};

 
  


 




  render() {
    return (
      <div>
        <button className="homeButton" onClick={this.toggle}>Créer Ordre de Mission</button>
        <Modal size='lg' isOpen={this.state.modal} toggle={this.toggle} className={this.props.className}>
          <ModalHeader toggle={this.toggle}> Ordre de Mission</ModalHeader>
          <ModalBody>
            
            <div className="ordreMission">

            <h2>Ordre de Mission</h2>
    <label>Numero d'ordre</label><h1> {this.state.numOrdre}</h1>
            <h2>Inspecteur</h2>
               <label>Nom et Prenom</label><input  name="NomPrenom"type="text"   onChange={this.onChange}/>
               <label>Qualification</label><input  name="Qualification"type="text"   onChange={this.onChange}/>
            <h2>Détails de la mission</h2>
               <label>Date Debut</label><input  name="dateDebut"type="text"   onChange={this.onChange}/>
               <label>Date Fin</label><input  name="dateFin"type="text"   onChange={this.onChange}/>
               <label>Durée de la visite</label><input  name="dureeVisite"type="text"   onChange={this.onChange}/>
               <label>Qualifications Exigées</label><input  name="qualificationExigees"type="text"   onChange={this.onChange}/>
            <h2>Détails du Projet</h2>
               <label>Lieu de la mission</label><input  name="lieuMission"type="text"   onChange={this.onChange}/>
               <label>Client</label>
               <form name="form1">
               <select id="mySelect" onChange={this.classSelect}>
                  <option value="--">--</option>
                  <option value="client1" >client1</option>
                  <option value="client2" >client2</option>
                  <Clients/>
                </select>
                </form>
               

               <label>Etendue</label><input  name="etendue"type="text"   onChange={this.onChange}/>
            <h2>Logistiques</h2>
               <label>Materiels recommandés</label><input  name="materiels"type="text"   onChange={this.onChange}/>
               <label>lieu</label><input  name="lieu"type="text"   onChange={this.onChange}/>
               <label>Mobilisation</label><input  name="mobilisation"type="text"   onChange={this.onChange}/>
               <label>logement</label><input  name="logement"type="text"   onChange={this.onChange}/>
               <label>Démobilisation</label><input  name="demobilisation"type="text"   onChange={this.onChange}/>

                
            
            
            
            </div>
            
         
            
          </ModalBody>
          <ModalFooter>
    <Button 
          disabled={this.state.isLoading} color="primary" 
          onClick={()=> {this.AddMission(); }}
          >
        { !this.state.isLoading &&  <span> Ajouter un Ordre</span>}
        { this.state.isLoading &&  <span><i class="fas fa-circle-notch fa-spin"></i> Ajouter un expert</span>}
    
    </Button>
            <Button color="secondary" onClick={this.toggle}>Annuler</Button>
          </ModalFooter>
        </Modal>
      </div>
    );
  }
}

export default ModalE;
