import React, { Component } from 'react'
import axios from 'axios';
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
import {Link} from 'react-router-dom'
import './modal.css'

export class Clients extends Component {
    constructor(props) {
        super(props)
    
        this.state = {
            clientCode:"",
            clientName:"",
             tab:[],
             isLoading: false, // variable pour l'animation du button 
             modal: false,
        }
        this.toggle = this.toggle.bind(this); // close modal after adding new expert
    }
    toggle() {
        // clear state 
        this.setState(prevState => ({
          modal: !prevState.modal,
          clientCode:"",
            clientName:"",
             tab:[],
        }))}
    onChange = (e) => {
        this.setState({
          [e.target.name]: e.target.value,
          
        });}

        componentDidMount (){

            axios
              .get("/clients")
              .then(res => this.setState({
                  tab : res.data  // get data from database named FalconData and put all data in variable called profile
              }))
              .catch(err =>console.log("err", err))
          }
    addclient = () => {
        const { clientCode,clientName } = this.state;
        axios
        .post("/addClient",{
            clientName:clientName,
            clientCode:clientCode,
        })
        .then(res => axios.get("/clients"),
        
      
      )
      .catch(err => alert(err));
    }
    onAddClient= () =>{
       }
    
    
    render() {
        return (
            <div>
                <button className="homeButton" onClick={this.toggle}>Ajouter client</button>
        <Modal size='lg' isOpen={this.state.modal} toggle={this.toggle} className={this.props.className}>
          <ModalHeader toggle={this.toggle}> Client</ModalHeader>
          <ModalBody>
          <label>Nom Client</label><input type="text" name="clientName" onChange={this.onChange}></input>
                <label>Code Client</label><input type="text" name="clientCode" onChange={this.onChange}></input>
                </ModalBody>
                <ModalFooter>
                    
    <Button 
          disabled={this.state.isLoading} color="primary" 
          onClick= {this.addclient,this.state.onAddClient}
          >
        { !this.state.isLoading &&  <span> Ajouter un Client</span>}
        { this.state.isLoading &&  <span><i class="fas fa-circle-notch fa-spin"></i> Ajouter un expert</span>}
    
    </Button>
    
            <Button color="secondary" onClick={this.toggle}>Annuler</Button>
          </ModalFooter>
        
                </Modal>
               
            </div>
        )
    }
}

export default Clients
