import '../App.css'





export default class Navigation extends Component {
    constructor(props) {
        super(props)
    
        this.state = {
             show:true
        }
    }
    
    logout(){
        localStorage.removeItem("token")
        this.setState({
            show:false
        })
        }
    render() {
         const token = loc
        return (
           <div className="nav">
               <ul className="nav__menu">
               <li className="nav__menu-item">
                     {/* <a><h1></h1></a> */}
                     <ul className="nav__submenu">
                         {!token ? ( <li className="nav__submenu-item ">login</li>
                         ): (<li className="nav__submenu-item " > <button onClick={this.logout()}>logout</button></li>)}
                        
                         
                         
                         </ul>
               </li>
               </ul>
           </div>
        )
    }
}
