import React, { Component } from 'react'
import { Page, Text, View, Document, StyleSheet, Image, } from '@react-pdf/renderer';
import { PDFViewer } from '@react-pdf/renderer';
import axios from 'axios';


export default class PrintOrdre extends Component {
    constructor(props) {
        super(props)
    
        this.state = {
            numOrdre: "",// nom d'expert
            NomPrenom:"",
            Qualification:"",       
            dateDebut:"",
            dateFin:"",
            dureeVisite:"",
            qualificationExigees:"",
            lieuMission:"",
            client:"",
            etendue:"",
            materiels:"",
            lieu:"",
            mobilisation:"",
            logement:"",
            demobilisation:"",
          }     
        
    }
    // get data from data base 
    componentDidMount () {
        axios.get("/missionsList/" + this.props.id ).then(res => this.setState({
          ...res.data  // obtenir toutes variables de la base de donn√©s et les envoyer au constructor 
        }
          ))}
    
    render() {
        return (
            <div  style={styles.container}>
                 <PDFViewer width="100%" height="900px">
                 <Document>
                        <Page size="A4" style={styles.page} >
                            <View style={style.body}>
                                <Image
                                    style={style.image}
                                    src={require('../Media/logo-falcon.png')}
                                />

                            </View>
            
                           






                        </Page>




                </Document>
                </PDFViewer>
                
            </div>
        )
    }
}
const styles = StyleSheet.create({
    body: {
      padding: 10
    },
    table: { 
      display: "table", 
      width: "auto", 
      borderStyle: "solid", 
      borderColor: '#bfbfbf',
      borderWidth: 1, 
      borderRightWidth: 0, 
      borderBottomWidth: 0 
    }, 
    tableRow: { 
      margin: "auto", 
      flexDirection: "row" 
    }, 
    tableColHeader: { 
      width: "25%", 
      borderStyle: "solid", 
      borderColor: '#bfbfbf',
      borderBottomColor: '#000',
      borderWidth: 1, 
      borderLeftWidth: 0, 
      borderTopWidth: 0
    },   
    tableCol: { 
      width: "25%", 
      borderStyle: "solid", 
      borderColor: '#bfbfbf',
      borderWidth: 1, 
      borderLeftWidth: 0, 
      borderTopWidth: 0 
    }, 
    tableCellHeader: {
      margin: "auto", 
      margin: 5, 
      fontSize: 12,
      fontWeight: 500
    },  
    tableCell: { 
      margin: "auto", 
      margin: 5, 
      fontSize: 10 
    }
  });
