import React, { Component } from 'react'
import axios from "axios";

export default class connectJWT extends Component {
    constructor(props) {
        super(props)
    
        this.state = {
             username:"",
             password:"",
        }
    }

    handleChange = e => {
        this.setState({ [e.target.name]: e.target.value });
      };

      login = () => {
        const { username, password } = this.state;
        axios
          .post("http://localhost:5000/login", { username, password })
          .then(res => {
            console.log(res.data.user);
            localStorage.setItem("token", res.data.token);
            // this.props.getusers(res.data.user)
            
            // this.props.history.push('/users')
          })
          .catch(err => alert("please check your informations"));
      };
      configtoken = () => {
        const token = localStorage.getItem("token");
        const config = {
          headers: {
            "Content-type": "application/json"
          }
        };
        
     
    
        if (token) {
          config.headers["Authorization"] = token;
        }
        // const config = { headers: { common: { Authorization: token } } };
        return config;
      };


      componentDidMount() {
    
        console.log(this.configtoken());
        axios
          .get("http://localhost:5000/current", this.configtoken())
          .then(res =>  console(res.data))
          .catch(err => console.log(err.response.data));
      }



    render() {
        return (
            <div>
                
            </div>
        )
    }
}
