import React, { Component } from 'react'
import { Page, Text, View, Document, StyleSheet, Image, } from '@react-pdf/renderer';
import { PDFViewer } from '@react-pdf/renderer';
import axios from 'axios';


export default class PrintOrdre extends Component {
    constructor(props) {
        super(props)
    
        this.state = {
            numOrdre: "",// nom d'expert
            NomPrenom:"",
            Qualification:"",       
            dateDebut:"",
            dateFin:"",
            dureeVisite:"",
            qualificationExigees:"",
            lieuMission:"",
            client:"",
            etendue:"",
            materiels:"",
            lieu:"",
            mobilisation:"",
            logement:"",
            demobilisation:"",
          }     
        
    }
    // get data from data base 
    componentDidMount () {
        axios.get("/missionsList/" + this.props.id ).then(res => this.setState({
          ...res.data  // obtenir toutes variables de la base de donnés et les envoyer au constructor 
        }
          ))}
    
    render() {
        return (
            <div  style={style.container}>
                 <PDFViewer width="100%" height="900px">
                 <Document>
                        <Page size="A4" style={style.page} >
                            <View style={style.body}>
                                <Image
                                    style={style.image}
                                    src={require('../Media/logo-falcon.png')}
                                />

                            </View>
            
                            <View>
                            <Text style={style.title}>lieu: {this.state.lieu}</Text>
                                    <Text style={style.title}>ordre : {this.state.numOrdre}</Text>
                                </View>
                                <View wrap={false}>
                                    <Text style={style.descriptions}>Experience</Text>
                                    
                                </View>

                                <Text style={style.textFooter}>Generated by Falcon</Text>
                        </Page>










                </Document>
                </PDFViewer>
                
            </div>
        )
    }
}
const style = StyleSheet.create({
    page:{
      
       
    },
    body: {
        borderTopColor:'black',
        borderWidth:2,
        borderStyle:'solid',
       
      margin: 10,
      padding: 10,
      textAlign:'center',
      fontSize:20,
      fontWeight:'bold',
        color: '#15317E'
    },
    info: {
            marginLeft:10,
            marginBottom:10,
            fontSize:12,
            
    },
    descriptions:{
        textAlign: 'center',
        margin:10,
        fontWeight: 'bold',
        color: '#15317E'
    },
     descriptionsText:{
       
       

        margin: 12,
        fontSize: 14,
        textAlign: 'justify',
     
               
       
        
        
       

    },
    options:{
        marginRight:20,
        marginLeft:20,
                fontSize:15,

    },
    container: {
        textAlign:'center',
        marginTop: 65,
    },
    image: {
        marginVertical: 25,
        marginHorizontal: 10,
        width:150,
        height:55,
    },
    hr:{
        borderBottomColor: 'black',
        borderBottomWidth: 1,
        marginHorizontal:10,
    },
    textFooter:{
        textAlign:'right',
        fontSize:8,
        marginTop:5,
    },
  });
