import React, { Component } from 'react'
import { Page, Text, View, Document, StyleSheet, Image, } from '@react-pdf/renderer';
import { PDFViewer } from '@react-pdf/renderer';
import axios from 'axios';


export default class PrintOrdre extends Component {
    constructor(props) {
        super(props)
    
        this.state = {
            numOrdre: "",// nom d'expert
            NomPrenom:"",
            Qualification:"",       
            dateDebut:"",
            dateFin:"",
            dureeVisite:"",
            qualificationExigees:"",
            lieuMission:"",
            client:"",
            etendue:"",
            materiels:"",
            lieu:"",
            mobilisation:"",
            logement:"",
            demobilisation:"",
          }     
        
    }
    // get data from data base 
    componentDidMount () {
        axios.get("/missionsList/" + this.props.id ).then(res => this.setState({
          ...res.data  // obtenir toutes variables de la base de donn√©s et les envoyer au constructor 
        }
          ))}
    
    render() {
        return (
            <div  style={style.container}>
                 <PDFViewer width="100%" height="900px">
                 <Document>
                        <Page size="A4" style={style.page} >
                            <View style={style.body}>
                                <Image
                                    style={style.image}
                                    src={require('../Media/logo-falcon.png')}
                                />

                            </View>
            
                            <View>
                                <table>
                                    
                            <Text style={style.title}>lieu: {this.state.lieu}</Text>
                                    <Text style={style.title}>ordre : {this.state.numOrdre}</Text>
                                    </table>
                                </View>
                                <View wrap={false}>
                                    <Text style={style.descriptions}>Experience</Text>
                                    
                                </View>

                                <Text style={style.textFooter}>Generated by Falcon</Text>
                        </Page>










                </Document>
                </PDFViewer>
                
            </div>
        )
    }
}
const style = StyleSheet.create({
    page:{
      
       
    },
    body: {
      },
    title: {
    },
    info: {
            
    },
    descriptions:{
        
    },
     descriptionsText:{     
    },
    options:{
            },
    container: {
        
    },
    image: {

        width:90,
        height:45,
    },
    hr:{
        
    },
    textFooter:{
        
    },
  });
