import React, { Component } from 'react'
import {Redirect, Link} from 'react-router-dom'
import "../App.css"
export default class navbar extends Component {
    constructor(props) {
        super(props)
    
        this.state = {
            mode:'non'
        }
        
    }
    componentDidMount() {
        const token = localStorage.getItem("token");
        if(token){
            this.setState({mode: 'oui'});
        }
        else{
            this.setState({mode: 'non'});
        }
       
      }
      logout = () => {
        localStorage.removeItem("token");
      };
      render () {
        const token = localStorage.getItem("token");
        return(       <div> 
         { !token ? (
            <div className="nav-container">
            <nav className="navbar">
            <Link to="/" ><img className="logo"src={require('../Media/logo-falcon.png')}></img></Link>
            <span className="applybtn" ><Link to="/"><button onClick={this.logout}>log out</button></Link></span> 
            </nav>   </div>
          ) :  (
            <div className="nav-container">
            <nav className="navbar">
            <Link to="/" ><img className="logo"src={require('../Media/logo-falcon.png')}></img></Link>
            {/*if you want to add a button on the right of navbar <span className="applybtn" ></span> */}
            </nav>   </div> 
          )
        }; </div>)
    }}
