import React, { Component } from 'react'
import { Page, Text, View, Document, StyleSheet, Image, } from '@react-pdf/renderer';
import { PDFViewer } from '@react-pdf/renderer';
import axios from 'axios';


export default class PrintOrdre extends Component {
    constructor(props) {
        super(props)
    
        this.state = {
            numOrdre: "",// nom d'expert
            NomPrenom:"",
            Qualification:"",       
            dateDebut:"",
            dateFin:"",
            dureeVisite:"",
            qualificationExigees:"",
            lieuMission:"",
            client:"",
            etendue:"",
            materiels:"",
            lieu:"",
            mobilisation:"",
            logement:"",
            demobilisation:"",
          }     
        
    }
    // get data from data base 
    componentDidMount () {
        axios.get("/missionsList/" + this.props.id ).then(res => this.setState({
          ...res.data  // obtenir toutes variables de la base de donn√©s et les envoyer au constructor 
        }
          ))}
    
    render() {
        return (
            <div  style={styles.container}>
                 <PDFViewer width="100%" height="900px">
                 <Document>
                        <Page size="A2" style={styles.page} >
                            <View style={styles.body}>
                                <Image
                                    style={styles.image}
                                    src={require('../Media/logo-falcon.png')}
                                />

                            </View>
            
                            <View>
                                    <Text style={styles.title}>{this.state.numOrdre.toLocaleUpperCase()}</Text>
                                </View>

                                <Text style={styles.textFooter}>Generated by Falcon</Text>
                        </Page>










                </Document>
                </PDFViewer>
                
            </div>
        )
    }
}
const styles = StyleSheet.create({
    page:{
        paddingTop: 30,
        paddingBottom: 35,
        paddingHorizontal: 25,
       
    },
    body: {
        borderTopColor:'black',
        borderWidth:2,
        borderStyle:'solid',
        height:'100%'
      },
    
    image: {
        marginVertical: 25,
        marginHorizontal: 10,
        width:150,
        height:55,
    },
    
    textFooter:{
        textAlign:'right',
        fontSize:8,
        marginTop:5,
    },
  });
